const Point = require('@mapbox/point-geometry');
const {mat4, vec4} = require('@mapbox/gl-matrix');
const symbolSize = require('./symbol_size');
const {addDynamicAttributes} = require('../data/bucket/symbol_bucket');






module.exports = {
    updateLineLabels,
    getLabelPlaneMatrix,
    getGlCoordMatrix
